mapper.update:
update tch_stu_course_open_rel
set SCHEDULE_ID = #{scheduleId,jdbcType=VARCHAR},
  COURSE_OPEN_ID = #{courseOpenId,jdbcType=VARCHAR},
  STU_ID = #{stuId,jdbcType=VARCHAR}
where ID = #{id,jdbcType=VARCHAR}

mapper.select:
select a.ID,b.REAL_NAME,b.IDENTITY_CARD,a.STU_ID,b.STU_NUMBER, b.CLASS_ID,b.GRADE as stuGrade,b.MAJOR_ID,c.COURSE_ID, c.CLASSROOM_ID, c.SCHOOL_YEAR,c.TERM, d.NAME as courseName ,(select NAME from sys_teacher_info where ID = d.TEACHER_ID) as teacherName ,(select TEXT from sys_dict_item where DICT_TYPE = 'TERM' and CODE = c.TERM) as termText ,(select NAME from tch_class_info where ID = b.CLASS_ID) as className ,(select NAME from sys_classroom where ID = c.CLASSROOM_ID) as classroomText ,(select name from sys_major where id = b.MAJOR_ID) as majorText ,(select TEXT from sys_dict_item where DICT_TYPE = 'USER_SEX' and CODE = b.SEX) as sexText from tch_stu_course_open_rel a,arc_student_info b,tch_course_open c,tch_course d where a.STU_ID=b.ID and a.COURSE_OPEN_ID=c.ID and c.COURSE_ID=d.ID
and c.ID = #{queryparam.courseOpenId,jdbcType=VARCHAR}
order by b.STU_NUMBER

select count(1)
from tch_stu_course_open_rel
where  1=1

  and COURSE_OPEN_ID = #{courseOpenId,jdbcType=VARCHAR}


  and STU_ID = #{stuId,jdbcType=VARCHAR}

select count(*) from ( select count(*) as cnt from tch_stu_course_open_rel a,tch_course_open b,tch_course c where 1=1 and a.COURSE_OPEN_ID=b.ID and b.COURSE_ID=c.ID and b.SCHOOL_YEAR=(SELECT SCHOOL_YEAR from tch_course_open where ID=#{courseOpenId,jdbcType=VARCHAR}) and b.TERM=(SELECT TERM from tch_course_open where ID=#{courseOpenId,jdbcType=VARCHAR})
and a.STU_ID = #{stuId,jdbcType=VARCHAR}
GROUP by c.SUBJECT_ID)T

select b.REAL_NAME,b.IDENTITY_CARD,a.STU_ID,a.COURSE_OPEN_ID,b.STU_NUMBER, b.CLASS_ID,b.GRADE as stuGrade,b.MAJOR_ID,c.COURSE_ID, c.CLASSROOM_ID, c.SCHOOL_YEAR,c.TERM, d.NAME as courseName ,(select NAME from sys_teacher_info where ID = d.TEACHER_ID) as teacherName ,(select TEXT from sys_dict_item where DICT_TYPE = 'TERM' and CODE = c.TERM) as termText ,(select NAME from tch_class_info where ID = b.CLASS_ID) as className ,(select NAME from sys_classroom where ID = c.CLASSROOM_ID) as classroomText ,(select name from sys_major where id = b.MAJOR_ID) as majorText ,(select TEXT from sys_dict_item where DICT_TYPE = 'USER_SEX' and CODE = b.SEX) as sexText from tch_stu_course_open_rel a,arc_student_info b,tch_course_open c,tch_course d where a.STU_ID=b.ID and a.COURSE_OPEN_ID=c.ID and c.COURSE_ID=d.ID
and a.COURSE_OPEN_ID = #{courseOpenId,jdbcType=VARCHAR}
and b.STATUS = #{stuStatus,jdbcType=VARCHAR}
order by b.STU_NUMBER

select count(1)
from tch_stu_course_open_rel a
where a.stu_id =  #{stuId,jdbcType=VARCHAR}
